// Fun√ß√£o recursiva para processar quebras de linha em objetos aninhados
function processNestedObject(obj) {
  if (typeof obj === 'string') {
    // Substitui todas as quebras de linha por \n literal
    return obj
      .replace(/(?:\r\n|\r|\n)/g, '\\n')
      .replace(/\\\n\\\n/g, '\\n')  // Remove duplica√ß√µes
      .replace(/\s*\\\n\s*/g, '\\n'); // Remove espa√ßos extras
  }

  if (Array.isArray(obj)) {
    return obj.map(item => processNestedObject(item));
  }

  if (obj !== null && typeof obj === 'object') {
    const processed = {};
    for (const key in obj) {
      processed[key] = processNestedObject(obj[key]);
    }
    return processed;
  }

  return obj;
}

// Processa cada item
return items.map(item => {
  // Processa todo o objeto json recursivamente
  const processedJson = processNestedObject(item.json);

  return {
    json: processedJson,
    // Mant√©m outros campos do item se existirem
    ...Object.keys(item).reduce((acc, key) => {
      if (key !== 'json') {
        acc[key] = item[key];
      }
      return acc;
    }, {})
  };
});

