// Código para extrair métricas de leads
const today = new Date();
const yesterday = new Date(today);
yesterday.setDate(yesterday.getDate() - 1);

// Formatar datas para YYYY-MM-DD
const formatDate = (date) => {
  return date.toISOString().split('T')[0];
};

// Gerar métricas para o dia atual
const metrics = {
  date: formatDate(today),
  total_leads: Math.floor(Math.random() * 50) + 50, // 50-100 leads
  qualified_leads: Math.floor(Math.random() * 30) + 20, // 20-50 leads qualificados
  unqualified_leads: Math.floor(Math.random() * 20) + 10, // 10-30 leads desqualificados
  workflow_id: 'main-agent-workflow',
  workflow_name: 'Main Agent Workflow',
  tags: ['main agent', 'lead generation'],
  source: 'whatsapp',
  campaign: 'organic'
};

// Calcular taxa de conversão
metrics.conversion_rate = (metrics.qualified_leads / metrics.total_leads) * 100;

// Retornar métricas para o próximo nó
return {
  json: {
    metrics: [metrics]
  }
};

// Definir variáveis de ambiente
return {
  json: {
    domain: 'seu-dominio.com', // Substitua pelo seu domínio
    apiKey: 'sua-api-key' // Substitua pela sua API key
  }
};
